cur_frm.add_fetch("company","code","cc")
frappe.ui.form.on("Sales Order","is_pro_forma" ,function(frm){

if (cur_frm.doc.is_pro_forma==1){
     cur_frm.set_value("naming_series","PFI.cc./CS/1617/")
} else {

    cur_frm.set_value("naming_series","SO.cc./CS/1617/")
    cur_frm.refresh_fields()
}
})

