frappe.ui.form.on("Journal Entry","refresh" ,function(frm){

if (cur_frm.doc.voucher_type=="Journal Entry"){
     cur_frm.set_value("naming_series","JV.cc./1617/")
} 
else if   (cur_frm.doc.voucher_type=="Cash Entry"){

    cur_frm.set_value("naming_series","CPV.cc./1617/")
    cur_frm.refresh_fields()
}
else if   (cur_frm.doc.voucher_type=="Debit Note"){

    cur_frm.set_value("naming_series","DBN.cc./1617/")
